class Solution:
    ## did it really dirty (I don't know ... sliding window algo should not be this slow)
    def longestPalindrome(self, s: str) -> str:
        if(len(s) == 1 or len(set(list(s))) == 1 or s == s[::-1]):
            return s
        if (s == ""):
            return ""
        i,j,res = 0,1,[]
        while (i < len(s) - 1):
            if(s[i:j] == s[i:j][::-1]):
                res.append(s[i:j])
                j += 1
            elif(s[i:j] != s[i:j][::-1]):
                j += 1
            if(j > len(s) - 1): 
                if(s[i:j] == s[i:j][::-1]):
                    res.append(s[i:j])
                    i += 1
                    j = i + 1
                i += 1
                j = i + 1
        x = sorted(res,key=len)
        if(len(x) == 1):return x[0]           
        else: return x[len(x) - 1]  
                